/*BLoco de Codigo*/
public class BlocoCodigo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double i, j, d;
		
		i = 5;
		j = 10;
		
		//o alvo desta instrução if é um bloco
		if(i != 0) {
			System.out.println("i does not equal zero");
			d = j / i;
			System.out.println("j / i is " + d);
		}
	}

}
